void sort_array(int arr[], int n) 
{ 
    // Loop through the given array 
	for (int i = 0; i < n - 1; i++) { 
		// Find the minimum element 
		int min_index = i; 
		for (int j = i + 1; j < n; j++) { 
			if (arr[j] < arr[min_index]) 
				min_index = j; 
		} 

		// Swap the found minimum element with the first element 
		int temp = arr[min_index]; 
		arr[min_index] = arr[i]; 
		arr[i] = temp; 
	} 

