public void Reverse(Node head)  
{  
    Node current = head;  
    Node next = null;  
    Node prev = null;  

    while(current != null)  
    {  
        // Store next  
        next = current.next;  

        // Reverse current node's pointer  
        current.next = prev;  

        // Move pointers one position ahead.  
        prev = current;  
        current = next;  
    }  
    head = prev;  
}

