public static int[] sortArray(int[] array) {  
    int temp;  
    int n = array.length; 
    for (int i = 0; i < n; i++) {  
        for (int j = 1; j < (n - i); j++) {  
            if (array[j - 1] > array[j]) { 
                //swap elements  
                temp = array[j - 1];  
                array[j - 1] = array[j];  
                array[j] = temp;  
            }  
        }  
    }  
    return array;  
}  

