Here is a program to implement a priority queue using a max heap in C++:

#include <iostream>
#include <queue> 

using namespace std; 

// A class for Max Heap 
class MaxHeap 
{ 
    public: 
    int *harr; // pointer to array of elements in heap 
    int capacity; // maximum possible size of max heap 
    int heap_size; // Current number of elements in max heap 

    // Constructor 
    MaxHeap(int capacity); 

    // to heapify a subtree with root at given index 
    void MaxHeapify(int ); 

    int parent(int i) { return (i-1)/2; } 

    // to get index of left child of node at index i 
    int left(int i) { return (2*i + 1); } 

    // to get index of right child of node at index i 
    int right(int i) { return (2*i + 2); } 

    // to extract the root which is the maximum element 
    int extractMax(); 

    // Increases key value of key at index i to new_val 
    void increaseKey(int i, int new_val); 

    // Inserts a new key 'k' 
    void insertKey(int k); 
}; 

// Inserts a new key 'k' 
void MaxHeap::insertKey(int k) 
{ 
    if (heap_size == capacity) 
    { 
        cout << "\nOverflow: Could not insertKey\n"; 
        return; 
    } 

    // First insert the new key at the end 
    heap_size++; 
    int i = heap_size - 1; 
    harr[i] = k; 

    // Fix the max heap property if it is violated 
    while (i != 0 && harr[parent(i)] < harr[i]) 
    { 
       swap(harr[i], harr[parent(i)]); 
       i = parent(i); 
    } 
} 

// Extracts the maximum key (key at root) from max heap 
int MaxHeap::extractMax() 
{ 
    if (heap_size <= 0) 
        return INT_MAX; 
    if (heap_size == 1) 
    { 
        heap_size--; 
        return harr[0]; 
    } 

    // Store the maximum value. 
    int root = harr[0]; 

    // Replace root with last element. 
    harr[0] = harr[heap_size-1]; 
    heap_size--; 
    MaxHeapify(0); 

    return root; 
} 

// A utility function to swap two elements 
void swap(int x, int y) {
    int temp = x; 
    x = y; 
    y = temp; 
}

// Constructor: Builds a heap from a given array a[] of given size 
MaxHeap::MaxHeap(int cap) 
{ 
    heap_size = 0; 
    capacity = cap; 
    harr = new int[cap]; 
} 

// A recursive method to heapify a subtree with the root at given index 
// This method assumes that the subtrees are already heapified 
void MaxHeap::MaxHeapify(int i) 
{ 
    int l = left(i); 
    int r = right(i); 
    int largest = i; 
    if (l < heap_size && harr[l] > harr[i]) 
        largest = l; 
    if (r < heap_size && harr[r] > harr[largest]) 
        largest = r; 
    if (largest != i) 
    { 
        swap(harr[i], harr[largest]); 
        MaxHeapify(largest); 
    } 
} 

// main function to test above functions 
int main() 
{ 
    MaxHeap h(11); 

    h.insertKey(3); 
    h.insertKey(2); 
    h.insertKey(15); 
    h.insertKey(5); 
    h.insertKey(4); 
    h.insertKey(45); 

    cout << h.extractMax() << " "; 
    cout << h.getMax() << " "; 
    h.increaseKey(2, 50); 
    cout << h.getMax(); 
    return 0; 
}

